var isTouchCreeper = false;
var creeper;
var bomNum = 0;
var canvas = document.createElement("canvas");
const ctx = canvas.getContext('2d');
const particleSize = 8;
const explosionSpeed = 2;
var creeperImg = "data:image/png;base64,";
var creeperBomingImg = "data:image/png;base64,";
window.onload = function() {
	document.addEventListener('touchstart',
	function(event) {
		if (isTouchCreeper == false) {
			isTouchCreeper = true;
			let touch = event.touches[0];
			let x = touch.clientX;
			let y = touch.clientY;
			creeper = document.createElement("img");
			creeper.src = creeperImg;
			creeper.width = "30";
			creeper.height = "30";
			creeper.style.position = "absolute";
			creeper.style.top = y + "px";
			creeper.style.left = x + "px";
			document.body.appendChild(creeper);
			boom();
		}
	});
}
function boom() {
	if (bomNum <= 6) {
		setTimeout(function() {
			creeper.width = "35";
			creeper.height = "35";
			creeper.src = creeperBomingImg;
			setTimeout(function() {
				creeper.width = "30";
				creeper.height = "30";
				creeper.src = creeperImg;
				bomNum++;
				boom();
			},
			125);
		},
		125);
	} else {
		bomNum = 0;
		creeper.src = creeperBomingImg;
		setTimeout(function() {
			creeper.width = "35";
			creeper.height = "35";
			creeper.src = creeperBomingImg;
			setTimeout(function() {
				creeper.width = "40";
				creeper.height = "40";
				creeper.src = creeperImg;
				setTimeout(function() {
					creeper.src = creeperBomingImg;
					setTimeout(function() {
						document.body.removeChild(creeper);
						canvas.width = "40";
						canvas.height = "40";
                        canvas.style.position = "absolute";
						canvas.style.top = creeper.style.top;
						canvas.style.left = creeper.style.left;
						document.body.appendChild(canvas);
						createParticles(20, 20);
						setTimeout(function() {
							document.body.removeChild(canvas);
							isTouchCreeper = false;
						},
						500);
					},
					500);
				},
				125);
			},
			125);
		},
		125);
	}
}
function Particle(x, y) {
	this.x = x;
	this.y = y;
	this.size = particleSize;
	this.speedX = Math.random() * explosionSpeed - explosionSpeed / 2;
	this.speedY = Math.random() * explosionSpeed - explosionSpeed / 2;
}
Particle.prototype.update = function() {
	this.x += this.speedX;
	this.y += this.speedY;
	this.size -= 0.1;
}
Particle.prototype.draw = function() {
	ctx.fillStyle = 'rgba(211, 211, 211, 0.8)';
	ctx.fillRect(this.x, this.y, this.size, this.size);
}
const particles = [];
function createParticles(x, y) {
	for (let i = 0; i < 20; i++) {
		particles.push(new Particle(x, y));
	}
}
function animate() {
	ctx.clearRect(0, 0, canvas.width, canvas.height);
	for (let i = 0; i < particles.length; i++) {
		particles[i].update();
		particles[i].draw();
		if (particles[i].size <= 0) {
			particles.splice(i, 1);
			i--;
		}
	}
	requestAnimationFrame(animate);
}
animate();